import React, { useEffect, useState } from "react";
import Card from "./Card";
import "../css/style.css";

const offersList = [
  {
    id: 1,
    position: "Web designer",
    level: "Middle",
    stack: ["Figma", "HTML", "Adobe"],
    salary: "60.000",
    location: "Moskow",
    company: "Yandex",
    color: "#fff1d5",
  },
  {
    id: 2,
    position: "Front-end Developer",
    level: "Senior",
    stack: ["React", "JavaScript", "Node.js"],
    salary: "110.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: "#d5f5ff",
  },
  {
    id: 3,
    position: "React Developer",
    level: "Junior",
    stack: ["React", "JavaScript", "Redux"],
    salary: "45.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: "#d5f5ff",
  },
  {
    id: 4,
    position: "QA Engineer",
    level: "Junior",
    stack: ["Testing", "SQL"],
    salary: "25.000",
    location: "Moskow",
    company: "Melnica",
    color: "#e3ffe4",
  },
  {
    id: 5,
    position: "Python",
    level: "Senior",
    stack: ["Python", "Git", "Docker"],
    salary: "103.000",
    location: "Saitn-Petersburg",
    company: "Russoft",
    color: "#81ecec",
  },
  {
    id: 6,
    position: "React Developer",
    level: "Junior",
    stack: ["React", "JavaScript", "Redux"],
    salary: "45.000",
    location: "Moscow",
    company: "Yandex",
    color: "#fff1d5",
  },
  {
    id: 7,
    position: "QA Engineer",
    level: "Junior",
    stack: ["Testing", "SQL"],
    salary: "25.000",
    location: "Moskow",
    company: "Melnica",
    color: "#e3ffe4",
  },
];

export default function List() {
  const [offers, setOffers] = useState(offersList);
  const [animation, setAnimation] = useState(false);

  useEffect(() => {
    console.log(`Animation state: ${animation}`);
  }, [animation]);

  function deleteCard(id) {
    setOffers(() => offers.filter((offer) => offer.id !== id));
  }

  function fillCardList(offers) {
    let listItems = Object.values(offers).map((offer) => offer);
    return (
      <ul className="card-list" id="card-list">
        {listItems.map((item) => {
          return (
            <li className="card-list-item" key={item.id}>
              <Card offer={item} onDelete={deleteCard} />
            </li>
          );
        })}
      </ul>
    );
  }

  function sayHello(state) {
    console.log(`Hello, ${state}`);
    if (state) {
      clearInterval(timerID);
    }
  }

  // let timerID = setInterval(sayHello, 1000, animation);

  return (
    <>
      <h2>Offers list</h2>
      {offers.length > 0 ? fillCardList(offers) : <p>Offers not found</p>}
      <button onClick={setAnimation((prev) => !prev)}>Scroll</button>
    </>
  );
}
