import React, { useState } from "react";
import {
  IoCloseCircleOutline,
  IoPersonAddOutline,
  IoInformationCircleOutline,
} from "react-icons/io5";

export default function Card({ offer, onDelete }) {
  const [detail, setDetail] = useState(false);

  const getDetail = () => {
    return (
      <>
        <hr />
        <IoInformationCircleOutline
          className="btn btn-about"
          onClick={() => console.log(`Detail valut ${detail}`)}
        />
      </>
    );
  };

  const styles = {
    backgroundColor: offer.color,
  };

  return (
    <div
      className="card"
      id={offer.id}
      onMouseEnter={() => setDetail((prev) => !prev)}
      onMouseLeave={() => setDetail((prev) => (prev = false))}
      style={styles}
    >
      <div className="btn-deck">
        <IoPersonAddOutline
          className="btn btn-apend"
          onClick={(event) => {
            document.getElementById(offer.id).style.border = "1px solid black";
            console.log(`Append ${offer.position}`);
          }}
        />
        <IoCloseCircleOutline className="btn btn-remove" />
      </div>
      <h2>{offer.position}</h2>
      <h3>{offer.level}</h3>
      <div className="stack-flex">
        {offer.stack.map((item) => (
          <p className="stack-item" key={item}>
            {item}
          </p>
        ))}
      </div>
      <h1>{offer.salary}â‚½</h1>
      <p>
        {offer.location} {offer.company}
      </p>
      {detail ? getDetail() : null}
    </div>
  );
}
