import React, { useEffect, useState } from "react";
import Card from "./Card";
import "../css/style.css";

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(1);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return state - 1;
      }
    });
  };

  const [jobs, setJobs] = useState([]);

  const getOffers = () => {
    return fetch("./offers.txt").then((response) => {
      return response
        .json()
        .then((data) => {
          console.log(data);
          return data;
        })
        .catch((err) => {
          console.log(err);
        });
    });
  };

  useEffect(() => {
    fetch("./offers.txt")
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      });
  });

  const getSlice = (offers) => {
    return offers.slice(firstContentIndex, lastContentIndex);
  };

  return (
    <>
      <div className="card-deck">
        <button onClick={() => changePage(false)}>Back</button>
        {getSlice(jobs).map((elem) => {
          return <Card value={elem} key={elem.id} />;
        })}
        <button onClick={() => changePage(true)}>Next</button>
      </div>
    </>
  );
}
