import React, { useEffect, useState } from "react";

const persons = [
  "levi",
  "stan",
  "roger",
  "penny",
  "leonard",
  "radjesh",
  "hovard",
  "sheldon",
  "max",
];

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(2);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return -1;
      }
    });
  };

  const setPageSafe = (pageNum) => {
    if (pageNum > pageCount) {
      setPage(pageCount);
    } else if (pageNum < 1) {
      setPage(1);
    } else {
      setPage(pageNum);
    }
  };

  useEffect(() => {
    console.log("deck render");
  }, [page]);

  return (
    <div>
      <h3>Card</h3>
      {persons.slice(firstContentIndex, lastContentIndex).map((elem) => {
        console.log(elem);
        return <div key={persons.indexOf(elem)}>{elem}</div>;
      })}
      <button>Next</button>
    </div>
  );
}
