import React, { useEffect, useState } from "react";
import Card from "./Card";
import "../css/style.css";
import raw from "./test.txt";

const offers = [
  {
    id: 1,
    position: "Web designer",
    level: "Middle",
    stack: ["Figma", "HTML", "Adobe"],
    salary: "60.000",
    location: "Moskow",
    company: "Yandex",
    color: "#fff1d5",
  },
  {
    id: 2,
    position: "Front-end Developer",
    level: "Senior",
    stack: ["React", "JavaScript", "Node.js"],
    salary: "110.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: "#d5f5ff",
  },
  {
    id: 3,
    position: "React Developer",
    level: "Junior",
    stack: ["React", "JavaScript", "Redux"],
    salary: "45.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: "#d5f5ff",
  },
  {
    id: 4,
    position: "QA Engineer",
    level: "Junior",
    stack: ["Testing", "SQL"],
    salary: "25.000",
    location: "Moskow",
    company: "Melnica",
    color: "#e3ffe4",
  },
  {
    id: 5,
    position: "Python",
    level: "Junior",
    stack: ["Python", "Django", "Postgresql"],
    salary: "125.000",
    location: "Saint-Petersburg",
    company: "Russoft",
    color: "#91e994",
  },
];

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(1);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return state - 1;
      }
    });
  };

  useEffect(() => {
    fetch(raw)
      .then((r) => r.text())
      .then((text) => console.log(text));
  }, [page]);

  const getSlice = (offers) => {
    return offers.slice(firstContentIndex, lastContentIndex);
  };

  return (
    <>
      <div className="card-deck">
        <button onClick={() => changePage(false)}>Back</button>
        {getSlice(offers).map((elem) => {
          return <Card value={elem} key={elem.id} />;
        })}
        <button onClick={() => changePage(true)}>Next</button>
      </div>
    </>
  );
}
