import * as fs from "fs";
import React, { useEffect, useState } from "react";
import Card from "./Card";
import "../css/style.css";

const colors = ["#fff1d5", "#d5f5ff", "#e3ffe4"];

const offers = () => {
  const fs = require("fs");
  let rawData = fs.read("./offers.json");
  let offers = JSON.parse(rawData);
  console.log(offers);
};

function offersToJson(object) {
  return JSON.stringify(object, null, 4);
}

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(1);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return state - 1;
      }
    });
  };

  useEffect(() => {}, [page]);
  const getSlice = (offers) => {
    return offers.slice(firstContentIndex, lastContentIndex);
  };

  return (
    <>
      <div className="card-deck">
        <button onClick={() => changePage(false)}>Back</button>
        {getSlice(offers).map((elem) => {
          return <Card value={elem} />;
        })}
        <button onClick={() => changePage(true)}>Next</button>
      </div>
    </>
  );
}
