import React, { useState } from "react";
import Card from "./Card";
import "../css/style.css";

const baseUrl = "https://reqres.in/api/users?page=1";

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(1);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const offersList = [
    {
      id: 1,
      position: "Web designer",
      level: "Middle",
      stack: ["Figma", "HTML", "Adobe"],
      salary: "60.000",
      location: "Moskow",
      company: "Yandex",
      color: "#fff1d5",
    },
    {
      id: 2,
      position: "Front-end Developer",
      level: "Senior",
      stack: ["React", "JavaScript", "Node.js"],
      salary: "110.000",
      location: "Ivanovo",
      company: "Gemforge",
      color: "#d5f5ff",
    },
    {
      id: 3,
      position: "React Developer",
      level: "Junior",
      stack: ["React", "JavaScript", "Redux"],
      salary: "45.000",
      location: "Ivanovo",
      company: "Gemforge",
      color: "#d5f5ff",
    },
    {
      id: 4,
      position: "QA Engineer",
      level: "Junior",
      stack: ["Testing", "SQL"],
      salary: "25.000",
      location: "Moskow",
      company: "Melnica",
      color: "#e3ffe4",
    },
    {
      id: 5,
      position: "Python",
      level: "Senior",
      stack: ["Python", "Git", "Docker"],
      salary: "103.000",
      location: "Saitn-Petersburg",
      company: "Russoft",
      color: "#81ecec",
    },
    {
      id: 6,
      position: "React Developer",
      level: "Junior",
      stack: ["React", "JavaScript", "Redux"],
      salary: "45.000",
      location: "Moscow",
      company: "Yandex",
      color: "#fff1d5",
    },
    {
      id: 7,
      position: "QA Engineer",
      level: "Junior",
      stack: ["Testing", "SQL"],
      salary: "25.000",
      location: "Moskow",
      company: "Melnica",
      color: "#e3ffe4",
    },
  ];

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return state - 1;
      }
    });
  };

  const getSlice = (offers) => {
    return offers.slice(firstContentIndex, lastContentIndex);
  };

  return (
    <>
      <div className="card-deck">
        <button onClick={() => changePage(false)}>Back</button>
        {getSlice(offersList).map((elem) => {
          return <Card value={elem} key={elem.id} />;
        })}
        <button onClick={() => changePage(true)}>Next</button>
      </div>
    </>
  );
}
