import React, { useEffect, useState } from "react";
import "../css/style.css";

const colors = ["#fff1d5", "#d5f5ff", "#e3ffe4"];

const offers = [
  {
    id: 1,
    position: "Web designer",
    level: "Middle",
    stack: ["Figma", "HTML", "Adobe"],
    salary: "60.000",
    location: "Moskow",
    company: "Yandex",
    color: colors[0],
  },
  {
    id: 2,
    position: "Front-end Developer",
    level: "Senior",
    stack: ["React", "JavaScript", "Node.js"],
    salary: "110.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: colors[1],
  },
  {
    id: 3,
    position: "React Developer",
    level: "Junior",
    stack: ["React", "JavaScript", "Redux"],
    salary: "45.000",
    location: "Ivanovo",
    company: "Gemforge",
    color: colors[1],
  },
  {
    id: 4,
    position: "QA Engineer",
    level: "Junior",
    stack: ["Testing", "SQL"],
    salary: "25.000",
    location: "Moskow",
    company: "Melnica",
    color: colors[2],
  },
];

export default function Deck({ count, contenPerPage }) {
  const [page, setPage] = useState(1);
  const pageCount = Math.ceil(count / contenPerPage);
  const lastContentIndex = page * contenPerPage;
  const firstContentIndex = lastContentIndex - contenPerPage;

  const changePage = (direction) => {
    setPage((state) => {
      if (direction) {
        if (state === pageCount) {
          return state;
        }
        return state + 1;
      } else {
        if (state === 1) {
          return state;
        }
        return state - 1;
      }
    });
  };

  useEffect(() => {}, [page]);
  const getSlice = (offers) => {
    return offers.slice(firstContentIndex, lastContentIndex);
  };

  return (
    <>
      <div className="card-deck">
        <button onClick={() => changePage(false)}>Back</button>
        {getSlice(offers).map((elem) => {
          return <Card value={elem} />;
        })}
        <button onClick={() => changePage(true)}>Next</button>
      </div>
    </>
  );
}
