import React, { useEffect, useState } from "react";
import { FcAbout } from "react-icons/fc";
import {
  IoCloseCircleOutline,
  IoChevronBackCircleOutline,
  IoChevronForwardCircleOutline,
  IoInformationCircleOutline,
} from "react-icons/io5";

export default function Card({ value }) {
  const [detail, setDetail] = useState(false);

  useEffect(() => {}, [detail]);

  const getDetail = () => {
    return (
      <>
        <hr />
        <IoInformationCircleOutline
          className="btn btn-about"
          onClick={() => console.log(`Detail valut ${detail}`)}
        />
      </>
    );
  };

  const styles = {
    backgroundColor: value.color,
  };

  return (
    <div
      className="card"
      id={value.id}
      onMouseEnter={() => setDetail((prev) => !prev)}
      onMouseLeave={() => setDetail((prev) => (prev = false))}
      style={styles}
    >
      <h2>{value.position}</h2>
      <h3>{value.level}</h3>
      <div className="stack-flex">
        {value.stack.map((item) => (
          <p className="stack-item" key={item}>
            {item}
          </p>
        ))}
      </div>
      <h1>{value.salary}â‚½</h1>
      <p>
        {value.location} {value.company}
      </p>
      {detail ? getDetail() : null}
    </div>
  );
}
